mapper.select:
SELECT member_id
FROM account
WHERE member_id = #{id}

mapper.insert:
INSERT INTO account
VALUES (
	null
	, #{id}
	, #{token.access_token}
	, #{token.refresh_token}
	, #{token.user_seq_no}
	, null
	, ""
	, ""
	, ""
	,""
	,""
)

mapper.update:
UPDATE account
SET
	access_token = #{token.access_token}
	, refresh_token = #{token.refresh_token}
	, user_seq_no = #{token.user_seq_no}
WHERE member_id = #{id}
