mapper.insert:
INSERT 
INTO member
VALUES (
	#{member_id}
	, #{member_passwd}
	, #{member_nick}


			, CONCAT(#{member_email1}, '@',#{member_email2})


			, #{member_email}


	, #{member_name}
	, #{member_birthday}
	, #{member_gender}
	, #{member_phone}
	, #{member_img}
	, 1
	, 1
	, now()
	, #{member_alert_status}
	)

mapper.select:
SELECT *
FROM member


		and member_id = #{member_id}



		and member_nick = #{member_nick}



		and member_email = CONCAT(#{member_email1},'@',#{member_email2})


		and member_phone = #{member_phone}

		and member_status = 1

SELECT * 
FROM member
WHERE member_email = #{member_email}
	AND member_name = #{member_name}
	AND member_status = '1'

SELECT *
FROM mail_auth


		WHERE mail_auth_email = #{param}


		WHERE member_id = #{param}

mapper.update:
UPDATE mail_auth
SET mail_auth_code = #{mail_auth_code}


		WHERE mail_auth_email = #{param}


		WHERE member_id = #{param}

mapper.delete:
DELETE FROM mail_auth		


		WHERE mail_auth_email = #{param}


		WHERE member_id = #{param}

mapper.select:
SELECT *
FROM mail_auth
WHERE member_id = #{member_id}
AND mail_auth_code = #{mail_auth_code}

mapper.update:
UPDATE member
SET member_passwd = #{member_passwd}
WHERE member_id = #{member_id}

mapper.select:
SELECT *
FROM member
WHERE member_email = #{member_email}
OR member_phone = #{member_phone}
