mapper.select:
SELECT * 
FROM bookmark
WHERE member_id = #{sId}
AND class_idx = #{class_idx}

SELECT res_member_count
FROM payment
WHERE class_idx = #{class_idx}

SELECT * 
FROM coupon
WHERE member_id = #{sId}
AND com_idx = #{comIdx}

SELECT * 
FROM following
WHERE member_id = #{sId}
AND com_idx = #{comIdx}

SELECT *
FROM cart
WHERE res_visit_date = #{resVisitDate} AND class_idx = #{classIdx} AND member_id = #{memberId}

mapper.update:
UPDATE cart
SET res_member_count = res_member_count + #{resMemberCount}
WHERE res_visit_date = #{resVisitDate} AND class_idx = #{classIdx} AND member_id = #{memberId}

mapper.delete:
DELETE FROM bookmark
WHERE member_id = #{sId}
AND class_idx = #{class_idx}
