mapper.select:
SELECT com_name
FROM company
WHERE com_idx = #{com_idx}

SELECT class_title
FROM class
WHERE class_idx = #{class_idx}

SELECT member_phone, member_email
FROM member
WHERE member_id = #{member_id}

SELECT COUNT(*)
FROM review
WHERE class_idx = #{class_idx}

	SELECT ROUND(AVG(review_score)/2,2)
	FROM review
    WHERE class_idx = #{class_idx}

